      text: "",
      tasks: [],
    };
    this.updateInput = this.updateInput.bind(this);
    this.addTask = this.addTask.bind(this);
  }

  updateInput(event) {
    this.setState((state, props) => ({
      text: event.target.value,
    }));
  }

